
public class TernaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//?: - ternary operator ? <true block>:<false block>
		int a=90;
		int b=889;
		int c=a>b?a:b;         //a>b is true then it will get in c otherwise b get placed in c 
		System.out.println(c);
	}

}
